-- ----------------------------
-- Table structure for note_likes
-- ----------------------------
DROP TABLE IF EXISTS `note_likes`;
CREATE TABLE `note_likes`  (
  `id` int NOT NULL AUTO_INCREMENT COMMENT '主键id',
  `note_id` int NOT NULL COMMENT '手记ID',
  `user_id` int NOT NULL COMMENT '用户ID',
  `created_at` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  PRIMARY KEY (`id`) USING BTREE,
  UNIQUE INDEX `uk_note_user`(`note_id` ASC, `user_id` ASC) USING BTREE,
  INDEX `idx_note_id`(`note_id` ASC) USING BTREE,
  INDEX `idx_user_id`(`user_id` ASC) USING BTREE,
  CONSTRAINT `note_likes_ibfk_1` FOREIGN KEY (`note_id`) REFERENCES `notes` (`id`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `note_likes_ibfk_2` FOREIGN KEY (`user_id`) REFERENCES `users` (`id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE = InnoDB AUTO_INCREMENT = 1 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_unicode_ci COMMENT = '手记点赞表' ROW_FORMAT = DYNAMIC;

-- ----------------------------
-- Records of note_likes
-- ----------------------------

-- ----------------------------
-- Table structure for notes
-- ----------------------------
DROP TABLE IF EXISTS `notes`;
CREATE TABLE `notes`  (
  `id` int NOT NULL AUTO_INCREMENT COMMENT '主键id',
  `title` varchar(200) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '手记标题（可选）',
  `content` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '手记内容',
  `user_id` int NOT NULL COMMENT '用户ID（外键关联users表）',
  `mood` enum('开心','平静','思考','感慨','兴奋','忧郁','愤怒','恐惧','惊讶','厌恶') CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '心情',
  `weather` varchar(20) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '天气',
  `location` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '地点',
  `tags` json NULL COMMENT '标签数组',
  `is_private` tinyint(1) NOT NULL DEFAULT 0 COMMENT '是否私密（0-公开，1-私密）',
  `reading_time` int NULL DEFAULT NULL COMMENT '预估阅读时间（分钟）',
  `view_count` int NOT NULL DEFAULT 0 COMMENT '查看次数',
  `like_count` int NOT NULL DEFAULT 0 COMMENT '点赞次数',
  `created_at` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `updated_at` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '更新时间',
  PRIMARY KEY (`id`) USING BTREE,
  INDEX `idx_user_id`(`user_id` ASC) USING BTREE,
  INDEX `idx_created_at`(`created_at` ASC) USING BTREE,
  INDEX `idx_is_private`(`is_private` ASC) USING BTREE,
  INDEX `idx_mood`(`mood` ASC) USING BTREE,
  CONSTRAINT `notes_ibfk_1` FOREIGN KEY (`user_id`) REFERENCES `users` (`id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE = InnoDB AUTO_INCREMENT = 7 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_unicode_ci COMMENT = '手记表' ROW_FORMAT = DYNAMIC;

-- ----------------------------
-- Records of notes
-- ----------------------------
INSERT INTO `notes` VALUES (6, '时光流转：静享与创造的篇章', '<p>在这流转的时光里，我在家中细品了一段恬静的岁月，沉浸在<a target=\"_blank\" rel=\"noopener noreferrer\" href=\"https://en.wikipedia.org/wiki/Tea_culture\">品茗</a>的艺术之中。指尖在键盘上起舞，敲击出一串串代码的韵律。</p><h3>静享时光</h3><p>闭门即是深山，我在此<strong>品茗</strong>，<em>聆听</em>茶叶在滚水中轻柔绽放的声音，仿佛整个世界都随之安静。</p><h3>编程之美</h3><p>代码，那是我与这个世界沟通的桥梁。<code>void main()</code>，标记了一切的起点。在屏幕前，我<strong>编织</strong>着逻辑的脉络，<em>创造</em>出一个又一个虚拟的现实。</p><h3>中秋赏月</h3><p>当秋意渐浓，月至中秋，分外皎洁。在这个古老的节日里，我仰望星空，<strong>赏月</strong>的仪式仿佛是与古今的智者们对话。</p><blockquote><p>月圆是中秋，心圆是幸福。</p></blockquote><p><img src=\"/uploads/general/2025/10/abc652f0497f29e924960967411e36b3.jpg\"></p>', 1, '平静', '☁️ 多云', '江苏·苏州', '[]', 0, 2, 16, 0, '2025-10-07 15:27:48', '2025-10-11 13:33:38');

