-- ----------------------------
-- Table structure for user_achievements
-- ----------------------------
DROP TABLE IF EXISTS `user_achievements`;
CREATE TABLE `user_achievements`  (
  `id` int NOT NULL AUTO_INCREMENT,
  `user_id` int NOT NULL,
  `achievement_id` int NOT NULL COMMENT '成就ID',
  `name` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '成就名称',
  `description` text CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '成就描述',
  `icon` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '成就图标',
  `category` enum('content','social','engagement','milestone') CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT 'milestone' COMMENT '成就分类',
  `unlocked` tinyint(1) NOT NULL DEFAULT 0 COMMENT '是否已解锁',
  `unlocked_at` datetime NULL DEFAULT NULL COMMENT '解锁时间',
  `progress` json NULL COMMENT '进度信息',
  `points` int NOT NULL DEFAULT 0 COMMENT '成就点数',
  `rarity` enum('common','rare','epic','legendary') CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT 'common' COMMENT '稀有度',
  `created_at` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP,
  `updated_at` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
  PRIMARY KEY (`id`) USING BTREE,
  INDEX `idx_user_achievements_user_id_unlocked`(`user_id` ASC, `unlocked` ASC) USING BTREE,
  INDEX `idx_user_achievements_category`(`category` ASC) USING BTREE,
  INDEX `idx_user_achievements_rarity`(`rarity` ASC) USING BTREE,
  INDEX `idx_user_achievements_achievement_id`(`achievement_id` ASC) USING BTREE,
  CONSTRAINT `fk_user_achievements_user_id` FOREIGN KEY (`user_id`) REFERENCES `users` (`id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE = InnoDB AUTO_INCREMENT = 3 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_unicode_ci COMMENT = '用户成就表' ROW_FORMAT = DYNAMIC;

