-- ----------------------------
-- View structure for v_ai_usage_stats
-- ----------------------------
DROP VIEW IF EXISTS `v_ai_usage_stats`;
CREATE ALGORITHM = UNDEFINED SQL SECURITY DEFINER VIEW `v_ai_usage_stats` AS select `ai_chats`.`user_id` AS `user_id`,cast(`ai_chats`.`created_at` as date) AS `date`,count(0) AS `chat_count`,sum(`ai_chats`.`tokens`) AS `total_tokens`,avg(`ai_chats`.`duration`) AS `avg_duration`,sum((case when (`ai_chats`.`status` = 'success') then 1 else 0 end)) AS `success_count`,sum((case when (`ai_chats`.`status` = 'failed') then 1 else 0 end)) AS `failed_count` from `ai_chats` group by `ai_chats`.`user_id`,cast(`ai_chats`.`created_at` as date);

-- ----------------------------
-- View structure for v_post_stats
-- ----------------------------
DROP VIEW IF EXISTS `v_post_stats`;
CREATE ALGORITHM = UNDEFINED SQL SECURITY DEFINER VIEW `v_post_stats` AS select `p`.`id` AS `id`,`p`.`title` AS `title`,`p`.`user_id` AS `user_id`,`p`.`status` AS `status`,`p`.`published_at` AS `published_at`,`p`.`view_count` AS `view_count`,count(distinct `pl`.`id`) AS `like_count`,count(distinct `c`.`id`) AS `comment_count`,count(distinct `pb`.`id`) AS `bookmark_count` from (((`posts` `p` left join `post_likes` `pl` on((`p`.`id` = `pl`.`post_id`))) left join `comments` `c` on(((`p`.`id` = `c`.`target_id`) and (`c`.`target_type` = 'post')))) left join `post_bookmarks` `pb` on((`p`.`id` = `pb`.`post_id`))) group by `p`.`id`;

-- ----------------------------
-- View structure for v_user_stats
-- ----------------------------
DROP VIEW IF EXISTS `v_user_stats`;
CREATE ALGORITHM = UNDEFINED SQL SECURITY DEFINER VIEW `v_user_stats` AS select `u`.`id` AS `id`,`u`.`username` AS `username`,`u`.`email` AS `email`,`u`.`role` AS `role`,count(distinct `p`.`id`) AS `post_count`,count(distinct `n`.`id`) AS `note_count`,count(distinct `c`.`id`) AS `comment_count`,count(distinct `ua`.`id`) AS `achievement_count` from ((((`users` `u` left join `posts` `p` on((`u`.`id` = `p`.`user_id`))) left join `notes` `n` on((`u`.`id` = `n`.`user_id`))) left join `comments` `c` on((`u`.`id` = `c`.`user_id`))) left join `user_achievements` `ua` on((`u`.`id` = `ua`.`user_id`))) group by `u`.`id`;

-- ----------------------------
-- Procedure structure for sp_cleanup_expired_data
-- ----------------------------
DROP PROCEDURE IF EXISTS `sp_cleanup_expired_data`;
delimiter ;;
CREATE PROCEDURE `sp_cleanup_expired_data`()
BEGIN
  DECLARE affected_rows INT DEFAULT 0;
  
  -- æ¸…ç†è¿‡æœŸçš„AIèŠå¤©è®°å½•
  DELETE FROM ai_chats
  WHERE created_at < DATE_SUB(NOW(), INTERVAL 3 MONTH)
  AND status = 'success';
  SET affected_rows = affected_rows + ROW_COUNT();
  
  -- æ¸…ç†è¿‡æœŸçš„ç”¨æˆ·æ´»åŠ¨
  DELETE FROM user_activities
  WHERE created_at < DATE_SUB(NOW(), INTERVAL 30 DAY);
  SET affected_rows = affected_rows + ROW_COUNT();
  
  -- æ¸…ç†å¤±è´¥çš„AIä»»åŠ¡
  DELETE FROM ai_tasks
  WHERE status = 'failed'
  AND created_at < DATE_SUB(NOW(), INTERVAL 7 DAY);
  SET affected_rows = affected_rows + ROW_COUNT();
  
  -- è¿”å›æ¸…ç†çš„è®°å½•æ•°
  SELECT affected_rows as total_cleaned;
END
;;
delimiter ;

-- ----------------------------
-- Procedure structure for sp_get_hot_posts
-- ----------------------------
DROP PROCEDURE IF EXISTS `sp_get_hot_posts`;
delimiter ;;
CREATE PROCEDURE `sp_get_hot_posts`(IN days INT,
  IN limit_count INT)
BEGIN
  SELECT 
    p.*,
    COUNT(DISTINCT pl.id) as like_count,
    COUNT(DISTINCT c.id) as comment_count
  FROM posts p
  LEFT JOIN post_likes pl ON p.id = pl.post_id
  LEFT JOIN comments c ON p.id = c.post_id
  WHERE p.status = 1
  AND p.published_at >= DATE_SUB(NOW(), INTERVAL days DAY)
  GROUP BY p.id
  ORDER BY (p.view_count * 0.3 + COUNT(DISTINCT pl.id) * 0.5 + COUNT(DISTINCT c.id) * 0.2) DESC
  LIMIT limit_count;
END
;;
delimiter ;

