-- ----------------------------
-- Table structure for note_likes
-- ----------------------------
DROP TABLE IF EXISTS `note_likes`;
CREATE TABLE `note_likes`  (
  `id` int NOT NULL AUTO_INCREMENT COMMENT '主键id',
  `note_id` int NOT NULL COMMENT '手记ID',
  `user_id` int NOT NULL COMMENT '用户ID',
  `created_at` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  PRIMARY KEY (`id`) USING BTREE,
  UNIQUE INDEX `uk_note_user`(`note_id` ASC, `user_id` ASC) USING BTREE,
  INDEX `idx_note_id`(`note_id` ASC) USING BTREE,
  INDEX `idx_user_id`(`user_id` ASC) USING BTREE,
  CONSTRAINT `note_likes_ibfk_1` FOREIGN KEY (`note_id`) REFERENCES `notes` (`id`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `note_likes_ibfk_2` FOREIGN KEY (`user_id`) REFERENCES `users` (`id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE = InnoDB AUTO_INCREMENT = 1 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_unicode_ci COMMENT = '手记点赞表' ROW_FORMAT = Dynamic;

-- ----------------------------
-- Records of note_likes
-- ----------------------------

-- ----------------------------
-- Table structure for notes
-- ----------------------------
DROP TABLE IF EXISTS `notes`;
CREATE TABLE `notes`  (
  `id` int NOT NULL AUTO_INCREMENT COMMENT '主键id',
  `title` varchar(200) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '手记标题（可选）',
  `content` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '手记内容',
  `user_id` int NOT NULL COMMENT '用户ID（外键关联users表）',
  `mood` enum('开心','平静','思考','感慨','兴奋','忧郁','愤怒','恐惧','厌恶','惊讶','难过') CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '心情',
  `weather` varchar(20) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '天气',
  `location` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '地点',
  `tags` json NULL COMMENT '标签数组',
  `is_private` tinyint(1) NOT NULL DEFAULT 0 COMMENT '是否私密（0-公开，1-私密）',
  `reading_time` int NULL DEFAULT NULL COMMENT '预估阅读时间（分钟）',
  `view_count` int NOT NULL DEFAULT 0 COMMENT '查看次数',
  `like_count` int NOT NULL DEFAULT 0 COMMENT '点赞次数',
  `created_at` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `updated_at` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '更新时间',
  `last_read_at` datetime NULL DEFAULT NULL COMMENT '最后阅读时间',
  PRIMARY KEY (`id`) USING BTREE,
  INDEX `idx_user_id`(`user_id` ASC) USING BTREE,
  INDEX `idx_created_at`(`created_at` ASC) USING BTREE,
  INDEX `idx_is_private`(`is_private` ASC) USING BTREE,
  INDEX `idx_mood`(`mood` ASC) USING BTREE,
  INDEX `idx_last_read_at`(`last_read_at` ASC) USING BTREE,
  CONSTRAINT `notes_ibfk_1` FOREIGN KEY (`user_id`) REFERENCES `users` (`id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE = InnoDB AUTO_INCREMENT = 15 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_unicode_ci COMMENT = '手记表' ROW_FORMAT = Dynamic;

-- ----------------------------
-- Records of notes
-- ----------------------------
INSERT INTO `notes` VALUES (8, '如果活着和死亡本质无异，那便也没有存在的意义。', '<p>我站在人潮的中央，思考日日重复的生活，我突然想，如果有一天，年轻和垂老都难以惊起心中的涟漪，一潭死水的沉闷，鲜花和蛋糕也撼动不了。</p><p>如果人开始，不能为了微小的事物而感动，那么地震和山洪的噩耗，想必也惊闻不了，如果活着和死亡本质无异，那便也没有存在的意义。</p><p style=\"text-align: right;\">-《人间与我》</p>', 1, '思考', '多云', '苏州', '[\"思考\"]', 0, 1, 58, 0, '2025-10-21 16:11:58', '2025-11-07 16:11:09', '2025-11-07 16:11:09');
INSERT INTO `notes` VALUES (9, '没去过山巅，只能看看夕阳落下屋檐。', '<p>没去过山巅，只能看看夕阳落下屋檐。</p><p>未曾涉足深海，只能想象浪涛拍打岸边的壮阔。</p><p>生活中总有些远方，是我们未曾到达的梦。</p><p>每个人心中都有一个向往的地方，或许是山巅，或许是深海。</p><p>向往，让我们对未来充满期待，给平凡的生活增添一抹色彩。</p><p>那些遥远的地方，似乎只能成为心中的一个遗憾。</p><p>但追逐梦想的脚步从未停歇，我们都在用自己的方式，努力向心中的远方靠近。</p><p>即使无法亲临山巅，我们依然可以在心中描绘出那片美丽的风景。</p>', 1, '平静', '雨天', '苏州', '[\"遗憾\"]', 0, 1, 60, 0, '2025-10-30 16:05:05', '2025-11-12 02:56:22', '2025-11-12 02:56:22');
INSERT INTO `notes` VALUES (10, '三盏热水伴雨时', '<p><span>窗玻璃被雨丝划得模糊，淅淅沥沥的声响裹着风，从缝隙里钻进来时，总带着点凉。晨起翻找厚外套时，指尖触到额头，才觉出那点不同寻常的温度 —— 像是揣了颗温吞的小石子，不灼人，却沉甸甸地压着精神。​</span></p><p><span>桌上的马克杯换了三次热水，水汽漫过杯沿，在键盘上凝出细小的水珠。指尖敲下去时，偶尔会顿一下，不是忘了字句，是鼻腔里的阻塞总让人忍不住吸一口气，连带眼眶也发潮。屏幕右下角的待办清单还亮着，红色的数字像未拆的信，明知该拆开一一回复，却总被喉咙里的痒意打断。​</span></p><p><span>起身去阳台收衣服，雨丝斜斜地溅在手腕上，凉得人一缩。晾衣绳上的衬衫还带着潮气，像此刻的呼吸，每一口都裹着点滞涩。远处的树影在雨雾里晃，叶片垂着水珠，倒比我精神些。​</span></p><p><span>回到书桌前，又端起马克杯。热水滑过喉咙时，终于有片刻的舒缓。窗外的雨还没停，清单上的条目也还在，只是此刻倒愿意慢些了 —— 等这阵潮意过去，等指尖再灵活些，那些没做完的事，总会在雨停之前，慢慢落定。</span></p>', 1, '思考', '雨天', '苏州', '[\"困\"]', 0, 3, 40, 0, '2025-11-06 13:56:39', '2025-11-13 03:34:04', '2025-11-13 03:34:04');
INSERT INTO `notes` VALUES (11, '方寸里的星辰——听《伟大的渺小》', '<p>深夜的风卷着半盏残灯，耳机里泄出林俊杰的歌声，“伟大并非凑巧，渺小是刻意煎熬”。音符像细沙穿过指缝，忽然惊觉这旋律里藏着的，原是每个人生命里都有的褶皱与光亮。我们总在烟火人间里低头赶路，却忘了那些被忽略的渺小瞬间，早已在时光里织就了伟大的轮廓。</p><p>仰望星空时，我们总被银河的壮阔震撼，却鲜少留意缀在天幕的每一颗星——它们或许遥远黯淡，连光都要跋涉千万年才抵达地球，可正是这无数渺小的闪烁，才铺就了整片苍穹的璀璨。就像林俊杰在《伟大的渺小》里唱的“夜晚星辰高调闪耀，也许是最后的燃烧”，自然的法则从不是以体量定轻重，而是让每一种渺小都在自己的轨迹里，完成独特的生命闭环。山巅的松柏是伟大的，可扎根石缝的苔藓，以纤细的根须分解岩石、滋养土壤，何尝不是另一种伟大？江海的奔涌是伟大的，可檐角的雨滴，以千万次坠落磨圆卵石、浸润土地，又何尝不是渺小的力量彰显？</p><p>人类文明的长河里，这种“渺小筑伟大”的密码从未断绝。我们铭记金字塔的雄伟，却不该忘记那些搬运巨石的工匠，是他们掌心的老茧、肩头的压痕，将一块块方石垒成永恒；我们赞叹航天事业的辉煌，却不能忽略无数科研工作者的坚守，是实验室里的微光、数据册上的批注，让航天器挣脱地心引力。小时候我们总误将“伟大”等同于“非凡”，以为必须是改变时代的英雄才算数，却不知英雄的身后，是无数匿名的渺小在托举。就像歌中“把客套 捍卫仅有的骄傲”，每个在自己领域咬牙坚持的人，或许都曾有过迷茫与挣扎，但正是这份不放弃的渺小执着，才汇聚成推动时代前行的磅礴力量。</p><p>那一瞬间，我忽然读懂了“渺小”的重量。我们总以为伟大是高山大海，却忘了一盏灯能照亮夜路，一滴水能滋养草木，就像歌里唱的“最渺小的我，有大大的梦”。那些为了三餐奔波的清晨，为了一个目标咬牙坚持的深夜，为了陌生人的困境驻足的瞬间，都是渺小生命里最动人的注脚。就像林俊杰在旋律里藏的转折，从低沉的呢喃到高亢的呐喊，恰如我们的人生，总要在接纳渺小之后，才能触碰到伟大的轮廓。</p><p>耳机里的歌声循环到最后一句，“我有多渺小 也做得到”。黎明穿透云层时，我看见城市在微光中苏醒：马路上的车流开始涌动，写字楼的窗次第亮起，工厂的机器传来低沉的轰鸣。这世间的大多数人，都在各自的岗位上过着重复的生活，没有聚光灯，没有掌声，就像一颗螺丝钉、一片铺路石，渺小到容易被忽略。可正是这无数“螺丝钉”的坚守，让交通网络畅行无阻；正是这无数“铺路石”的支撑，让社会机器稳步运转。伟大从不是孤胆英雄的独角戏，而是千万渺小生命的合唱。</p><p>原来伟大从不是刻意追求的远方，而是藏在每一个渺小的当下。就像这首歌，旋律终会落幕，但那些关于生命的感悟，会像种子一样在心里发芽——做一株扎根大地的小草，也能在风雨里舒展腰肢，在阳光下绽放绿意，这便是渺小最伟大的模样。</p>', 1, '思考', '多云', '苏州', '[\"音乐\"]', 0, 6, 0, 0, '2025-11-12 13:22:13', '2025-11-12 13:22:18', '2025-11-12 13:22:18');
INSERT INTO `notes` VALUES (12, '时光的注脚——听《光阴副本》', '<p>暮色漫过书桌时，林俊杰的《光阴副本》轻轻漫进耳廓。“如果回忆能够誊写，光阴就有了副本”，这句藏在旋律里的注解，让代码编辑器的光标骤然停驻，目光落在屏幕角落的项目日志上——那是半年前参与首个上线项目的启动日期。原来光阴从不是单向执行的线性程序，它会悄悄为每个关键节点生成快照，藏在吉他的拨弦声中，也藏在我们敲击过千万次的键盘缝隙里。</p><p>“副本”是个多妙的词，像时光为人生做的增量备份。就像代码仓库里的版本控制记录，即便过了数月，回溯提交日志依然能看见初入职场的青涩：调试到凌晨的服务器机房，显示屏的蓝光映着脸上的疲惫，却在看到“部署成功”提示时瞬间亮起眼睛；团队评审会上的原型图，标注着密密麻麻的修改意见，那些争论与妥协，如今都成了优化迭代的关键依据；还有上线前共享的那杯冰咖啡，咖啡因驱散的困倦里，藏着一群人并肩作战的温度。这些细碎的片段，都被光阴制成了专属副本，在某个被旋律触动的瞬间，便完整地加载在记忆的终端。</p><p>歌里虽无直白表述，却用“Memories overflow, Where the past still lies”的意境诉说着过往未远，深以为然。我们总以为时光会清空缓存，却在某个不经意的瞬间被旧文件击中：翻到入职时的学习笔记，代码示例旁有导师标注的“边界条件需补充”；听到曾经调试时循环播放的歌单，立刻就能想起排查内存泄漏时的焦灼与释然；路过公司楼下的便利店，老板一句“还是老样子，冰美式不加糖？”，瞬间就能唤醒那些赶早班的清晨记忆。这些被光阴复刻的细节，比我们自己更记得那些平凡日子里的温度。它们不是冗余的日志，而是鲜活的注释，证明我们曾那样专注地敲下每一行代码，认真地对待每一个需求。</p><p>有人说光阴是最无情的重构工具，可《光阴副本》却让我读懂它的温柔。它会把Bug酿成调试的经验，把报错磨成优化的思路。曾经为一个需求的实现方式争执不下，如今才明白，不同的技术路径都是为了更优的用户体验。那些当时以为跨不过去的技术瓶颈，在光阴的副本里，都成了标注“成长”的里程碑。就像歌中起伏的旋律，从低沉的回望到高亢的释然，恰如我们与代码的相处——从畏惧报错到从容Debug，从青涩新人到能独当一面的开发者。</p><p>“A turn of a page, I\'m leaving the cage”，歌声渐入高潮时，窗外的写字楼亮起成片的灯光。忽然明白，光阴的美好不仅在于留存过往，更在于它让每个当下都成为“崭新篇章”的序章。我们回溯过往的“开发日志”，是为了积累技术沉淀，而不是困在旧的代码框架里停滞不前。就像此刻，耳机里的旋律还在流淌，编辑器里的代码尚在调试，屏幕上未完成的功能模块，正等着被我们敲成新的人生代码。</p><p>夜渐深，歌声循环至尾声。关闭项目日志，指尖抚过熟悉的机械键盘，却觉得心里暖意融融。那些被光阴复刻的副本，是我们职业生涯最珍贵的技术栈。它们呼应着歌中“举凡无法抹灭，但凡不可挽回，皆需全然接受”的通透——不必纠结过往的Bug，不必焦虑未来的需求，只需认真写好当下的每一行代码。因为此刻的每一次调试、每一次提交，都会被光阴精心存档，在未来的某一天，成为我们“踏出牢笼”、从容前行的底气。</p>', 1, '平静', '多云', '苏州', '[\"音乐\"]', 0, 7, 21, 0, '2025-11-12 13:25:00', '2025-11-15 04:54:15', '2025-11-15 04:54:15');
INSERT INTO `notes` VALUES (13, '代码里的微光——听《幸存者》', '<p>凌晨两点的办公室，只有显示器的冷光和键盘的轻响。林俊杰的《幸存者》突然从降噪耳机里涌出来，“背着梦的幸存者，经过现实的拉扯”，指尖在回车键上顿住，屏幕上“运行成功”的绿色提示框，像暗夜里炸开的星点，瞬间烫热了发麻的指尖。原来所谓“幸存”，从不是幸运的豁免，而是在崩溃边缘多撑一秒的执着——就像调试代码时，多试一次参数的坚持。</p><p>作为刚毕业的程序员，入职后的第一个独立模块，就给了我一场“生存考验”。需求文档里的“高并发兼容”像座没标路径的山，第一次部署到测试环境就触发了死锁，日志里刷满的红色报错，比深夜的写字楼更让人窒息。我对着代码逐行断点调试，从数据库连接池到线程池配置，改到晨光爬上屏幕时，眼里只剩代码的残影。那时耳机里循环的就是《幸存者》，“得对自己比别人对我严格”，这句歌词像根细针，轻轻扎醒了快要麻木的神经——那些报错不是终点，是指引问题的光。</p><p>歌里唱“有苦涩忐忑坎坷，忍着有了起色”，这话在第三次上线失败时，有了最真切的体会。那次因为忽略了边缘场景的异常处理，模块在高并发压力测试中突然宕机，领导的消息简洁直接：“复盘问题，明天给方案”。我抱着电脑躲进茶水间，冻得发僵的手捏着冷掉的咖啡，忽然想起大学时做课程设计，也是这样对着报错哭丧着脸，导师却笑着说“报错不可怕，它在告诉你‘这里需要优化’”。那天我把模块拆成二十个小单元逐个排查，终于在某个循环逻辑里找到隐患——就像歌里起伏的旋律，低沉的挣扎过后，总会迎来高亢的突破。</p><p>“幸存者”从不是孤独的战场。在我对着死锁日志抓头发时，隔壁工位的前辈悄悄发来他之前处理类似问题的代码笔记，注释里标着“踩过的坑，供你参考”；测试小姐姐主动帮我梳理边缘场景，列满了三页纸的测试用例；连平时严肃的领导，也在下班时丢给我一包能量棒：“别急，问题解决了再下班”。这些细碎的温暖，就像代码里的“异常处理机制”，在我快要崩溃的节点接住我。而歌里“只是为了得到爱的人认可”的期许，此刻有了新的注解——职场里的“幸存”从不是单打独斗的硬扛，更是为了不辜负身边人的支持与信任。</p><p>当模块终于稳定上线，用户反馈里出现“流畅好用”的评价时，我又点开了《幸存者》。旋律里藏着的那些挣扎与坚持，突然和自己的经历重叠：第一次独立解决线上故障后的踏实，改了十版方案终于通过后的释然，和团队一起熬夜上线后的欢呼。这些瞬间串联起来，就是职场新人的“幸存”轨迹——不是打败了所有困难，而是在困难面前，没有选择放弃。</p><p>天快亮时，歌声停在“我是否已赚取了资格，可以站在这”。我关掉编辑器，看着窗外逐渐亮起的天际线，楼下的早餐店开始飘出香气。原来“幸存”从来不是终点，而是带着过往的经验继续前行的勇气。就像那些被攻克的Bug，都会变成未来开发路上的铠甲；那些一起扛过的深夜，都会成为职场里最珍贵的回忆。新的需求已经派下来，键盘又将响起新的节奏，而我知道，只要秉持着“幸存者”的执着与严格，就不怕前路再有新的挑战。</p>', 1, '开心', '晴天', '苏州', '[\"音乐\"]', 0, 7, 55, 0, '2025-11-13 12:30:47', '2025-11-15 16:51:37', '2025-11-15 16:51:37');
INSERT INTO `notes` VALUES (14, '代码裂缝里的光——我的实习经历', '<p>抱着键盘走出写字楼的那天，上海的秋风刚好吹散连日的阴霾。耳机里林俊杰的《裂缝中的阳光》缓缓流淌，“有多少创伤卡在咽喉，有多少眼泪滴湿枕头”，这句歌词像一把温柔的钥匙，打开了五个月来被焦虑与坚韧填满的记忆匣子。从八月带着憧憬踏入职门，到此刻卸下重负的轻松，这段实习经历，终在挣扎与坚持中，成了我职场路上最深刻的“进阶”课。</p><p>初到公司时的我，是揣着满身信心的“新兵”。当被告知负责一个中型项目时，每天都是第一个守在工位前，键盘敲击声成了办公室最早的晨曲。那时总觉得，只要足够努力，就能在代码世界里站稳脚跟。直到那段3000多行的“屎山代码”横在面前，我才读懂职场的复杂远超想象——150多行无注释的全局变量像乱麻缠绕，毫无预兆的return语句藏着无数陷阱，而向代码原主请教时换来的，却是一次又一次的羞辱。</p><p>“被懂的痛，只能沉默”，歌里的这句唱词，成了那段日子的注脚。我开始每天泡在代码里，逐行梳理逻辑，电脑配置太低导致开发工具频繁崩溃，就趁着崩溃间隙整理笔记；需求模糊且反复变更，就自己画流程图标注每一次修改。深夜的办公室里，只有显示器的冷光陪着我，加班到九点的20块餐补，是无数个夜晚最实在的慰藉。有好几次改bug到凌晨，看着窗外沉寂的街道，连“0点后晚到一小时”的考勤规则，都成了支撑下去的微小盼头。</p><p>项目开发中被频繁抽去支援其他工作，主项目延期却不能说明原因；测试总在下班时抛来一堆bug，深夜的电话总能准时打断刚躺下的疲惫；所有技术方案都要由XX定夺，我们提出的想法从来石沉大海。这些细碎的磋磨，像代码里的兼容性问题，毫无预兆却必须立刻解决。最绝望时，我盯着屏幕上的报错信息，连离职的念头都翻涌过无数次——可想到前期投入的房租与车费，想到高强度工作后连找新工作的精力都没有，又只能咬着牙继续。</p><p>“不要害怕生命中不完美的角落”，吴青峰的词在林俊杰的歌声里，渐渐照进心里的裂缝。我不再寄望于他人的帮助，开始独立拆解问题，周末和下班空隙里，从零构建出一套相同功能的项目。当新的代码流畅运行的那一刻，我突然明白，那些被羞辱的时刻、独自攻坚的夜晚，都在悄悄锤炼着我的能力。就像歌里唱的“阳光在每个裂缝中散落”，困境的缝隙里，藏着成长的光。</p><p>新项目的到来，成了证明自己的机会。和身边伙伴搭档的日子，少了孤军奋战的压抑，我全心投入，终于顺利完成开发。可离职前一天，主管突然要求我整理所有经手项目的路径信息，瞬间点燃了积压已久的情绪。我质问他为何我接手时无人对接，请教时屡遭冷遇，得到的却是老板“30分钟就能搞定”的质疑。那一刻眼泪在眼眶里打转，却硬生生憋了回去——“心脏没有那么脆弱，总还会有执着”，这份执着，是不想让过往的坚持都成笑话。</p><p>最终还是连夜整理完了资料，不是妥协，而是为自己的付出画上句点。离职那天抱着键盘走出公司，阳光落在肩头，突然读懂《进阶》里“跨越了浩劫，曙光渐亮”的重量。这段实习没有鲜花与认可，却让我在“屎山代码”里学会了拆解问题，在无人支持时懂得了自我支撑，在不公待遇中守住了尊严。那些流过的泪、熬过的夜，都成了“重生的力量”，让我更清楚自己要走的路。</p><p>耳机里的歌声换到“等到黑夜翻面之后，会是新的白昼”，我抬头望向上海的天空，云隙间的阳光格外明亮。</p>', 1, '难过', '雨天', '上海', '[\"成长\"]', 0, 7, 35, 0, '2024-12-31 12:37:51', '2025-11-15 11:18:03', '2025-11-15 11:18:03');

-- ----------------------------
-- Table structure for post_bookmarks
-- ----------------------------
DROP TABLE IF EXISTS `post_bookmarks`;
CREATE TABLE `post_bookmarks`  (
  `id` int NOT NULL AUTO_INCREMENT,
  `post_id` int NOT NULL COMMENT '文章ID',
  `user_id` int NOT NULL COMMENT '用户ID',
  `created_at` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  PRIMARY KEY (`id`) USING BTREE,
  UNIQUE INDEX `uk_post_user`(`post_id` ASC, `user_id` ASC) USING BTREE,
  INDEX `idx_post_id`(`post_id` ASC) USING BTREE,
  INDEX `idx_user_id`(`user_id` ASC) USING BTREE,
  CONSTRAINT `fk_post_bookmarks_postId` FOREIGN KEY (`post_id`) REFERENCES `posts` (`id`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `fk_post_bookmarks_userId` FOREIGN KEY (`user_id`) REFERENCES `users` (`id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE = InnoDB AUTO_INCREMENT = 2 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_unicode_ci COMMENT = '文章收藏表' ROW_FORMAT = Dynamic;

-- ----------------------------
-- Records of post_bookmarks
-- ----------------------------

-- ----------------------------
-- Table structure for post_likes
-- ----------------------------
DROP TABLE IF EXISTS `post_likes`;
CREATE TABLE `post_likes`  (
  `id` int NOT NULL AUTO_INCREMENT,
  `post_id` int NOT NULL COMMENT '文章ID',
  `user_id` int NOT NULL COMMENT '用户ID',
  `created_at` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  PRIMARY KEY (`id`) USING BTREE,
  UNIQUE INDEX `uk_post_user`(`post_id` ASC, `user_id` ASC) USING BTREE,
  INDEX `idx_post_id`(`post_id` ASC) USING BTREE,
  INDEX `idx_user_id`(`user_id` ASC) USING BTREE,
  CONSTRAINT `fk_post_likes_postId` FOREIGN KEY (`post_id`) REFERENCES `posts` (`id`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `fk_post_likes_userId` FOREIGN KEY (`user_id`) REFERENCES `users` (`id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE = InnoDB AUTO_INCREMENT = 4 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_unicode_ci COMMENT = '文章点赞表' ROW_FORMAT = Dynamic;

