-- ----------------------------
-- Table structure for posts
-- ----------------------------
DROP TABLE IF EXISTS `posts`;
CREATE TABLE `posts`  (
  `id` int NOT NULL AUTO_INCREMENT COMMENT 'id',
  `title` varchar(200) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL,
  `content` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL,
  `summary` text CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL,
  `cover_image` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL,
  `status` int NULL DEFAULT 0,
  `audit_status` tinyint NOT NULL DEFAULT 0 COMMENT '审核状态（0-待审核，1-审核通过，2-审核驳回）',
  `view_count` int NULL DEFAULT 0,
  `like_count` int NULL DEFAULT 0,
  `type_id` bigint NOT NULL COMMENT '分类id',
  `user_id` int NOT NULL COMMENT '作者ID（外键关联users表）',
  `published_at` datetime NULL DEFAULT NULL COMMENT '发布时间',
  `last_read_at` datetime NULL DEFAULT NULL COMMENT '最后阅读时间',
  `created_at` datetime NOT NULL COMMENT '创建时间',
  `updated_at` datetime NOT NULL COMMENT '更新时间',
  PRIMARY KEY (`id`) USING BTREE,
  INDEX `idx_userId`(`user_id` ASC) USING BTREE,
  INDEX `idx_status_publishedAt`(`status` ASC, `published_at` ASC) USING BTREE,
  INDEX `idx_categroupId`(`type_id` ASC) USING BTREE,
  INDEX `idx_audit_status`(`audit_status` ASC) USING BTREE,
  INDEX `idx_status_audit`(`status` ASC, `audit_status` ASC) USING BTREE,
  INDEX `idx_last_read_at`(`last_read_at` ASC) USING BTREE,
  CONSTRAINT `posts_ibfk_1` FOREIGN KEY (`user_id`) REFERENCES `users` (`id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE = InnoDB AUTO_INCREMENT = 12 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_unicode_ci ROW_FORMAT = Dynamic;

-- ----------------------------
-- Records of posts
-- ----------------------------
INSERT INTO `posts` VALUES (3, 'Python 入门教程', '<h2>Python入门教程</h2><p>Python是一种广泛应用于Web开发、数据分析、人工智能等领域的解释型、高级编程语言。它因其易读性、简洁的语法和强大的功能而广受欢迎。以下是一个详细的Python入门教程，旨在帮助初学者掌握基础知识，顺利踏入Python编程的世界。</p><h3>1. Python安装</h3><ol><li><p>访问Python官方网站下载安装包，根据您的操作系统选择合适的版本。</p></li><li><p>安装过程中，确保选中“Add Python to PATH”选项，以便在命令行中直接运行Python。</p></li><li><p>安装完成后，打开命令行工具，输入 <code>python</code> 并按Enter键，若出现Python解释器提示符，则表示安装成功。</p></li></ol><h3>2. Python基础语法</h3><h4>2.1 变量和数据类型</h4><ul><li><p>整数（Int）: 如 1, 100, -10</p></li><li><p>浮点数（Float）: 如 3.14, -0.001</p></li><li><p>字符串（String）: 如 \'Hello\', \"Python\"</p></li><li><p>布尔值（Boolean）: True, False</p></li></ul><h4>2.2 运算符</h4><p>Python支持多种运算符，包括算术运算符、比较运算符、逻辑运算符等。以下是一些常见运算符的示例：</p><ul><li><p>算术运算符：+（加）、-（减）、*（乘）、/（除）、//（整除）、%（取模）</p><pre><code class=\"language-python\">print(2 + 3)  # 输出5\nprint(5 / 2)  # 输出2.5\nprint(5 // 2) # 输出2\nprint(5 % 2)  # 输出1</code></pre></li><li><p>比较运算符：==（等于）、!=（不等于）、&gt;（大于）、&lt;（小于）、&gt;=（大于等于）、&lt;=（小于等于）</p></li><li><p>逻辑运算符：and（与）、or（或）、not（非）</p></li></ul><h4>2.3 控制流程</h4><ul><li><p>条件语句（if-elif-else）</p><pre><code class=\"language-python\">age = 20\nif age &lt; 18:\n    print(\"未成年\")\nelif age &lt;= 60:\n    print(\"成年\")\nelse:\n    print(\"老年人\")</code></pre></li><li><p>循环语句（for、while）</p><pre><code class=\"language-python\"># 计算1到10的阶乘之和\nfactorial_sum = 0\nfor i in range(1, 11):\n    factorial = 1\n    for j in range(1, i+1):\n        factorial *= j\n    factorial_sum += factorial\nprint(factorial_sum)  # 输出4037913</code></pre></li></ul><h3>3. 函数和模块</h3><h4>3.1 自定义函数</h4><p>函数是组织好的、可重复使用的代码块，用于执行单一、相关联的任务。</p><pre><code class=\"language-python\">def greet(name):\n    return f\"Hello, {name}!\"\n\nprint(greet(\"Alice\"))  # 输出\"Hello, Alice!\"</code></pre><p></p>', '适合初学者的Python基础教程', 'python-basics.jpg', 1, 1, 3342, 23, 1, 1, '2025-10-22 15:47:05', '2025-11-08 10:25:48', '2025-01-27 09:15:00', '2025-11-08 10:25:48');
INSERT INTO `posts` VALUES (4, '[Vite]使用PostCss实现响应式', '<h3>1、安装依赖</h3><p>首先，需要安装 <code>postcss</code> 和 <code>postcss-px-to-viewport</code> 这两个依赖。你可以使用 npm 或 yarn 来安装它们：</p><pre><code class=\"language-javascript\">npm install postcss postcss-px-to-viewport --save-dev</code></pre><h3>2、配置PostCss</h3><p>在 Vite 工程中，PostCSS 的配置通常是在 <code>vite.config.js</code> 或 <code>vite.config.ts</code> 文件中进行的。你需要在这个文件中添加 PostCSS 的配置，并引入 <code>postcss-px-to-viewport</code> 插件。</p><p>以下是一个示例配置：</p><pre><code class=\"language-javascript\">//vite.config.js\nimport { defineConfig } from \'vite\';\n//引入postcss-px-to-viewport\nimport postcss from \'postcss\';\nimport pxToViewport from \'postcss-px-to-viewport\';\n\nexport default defineConfig({\n  css: {\n    postcss: {\n      plugins: [\n        pxToViewport({\n          unitToConvert: \'px\', // 需要转换的单位，默认为\'px\'\n          viewportWidth: 375,  // 基准视口宽度（对应设计稿的宽度）\n          viewportHeight: 667, // 视口高度（可选）\n          unitPrecision: 5,    // px转换为vw的小数位数（很多情况下用5）\n          propList: [\'*\'],     // 指定需要转换的属性，\'*\'表示全部转换，\'font-size\'表示只转换font-size属性\n          viewportUnit: \'vmin\',  // 指定需要转换成的视口单位，默认vw\n          fontViewportUnit: \'vmin\', // 字体使用的视口单位，默认vw\n          selectorBlackList: [], // 指定不转换为视口单位的类名，用逗号隔开\n          minPixelValue: 1,    // 小于或等于1px不转换为视口单位，默认1\n          mediaQuery: false,   // 允许在媒体查询中转换px\n          replace: true,       // 是否直接替换而不是添加后缀\n          exclude: /node_modules/ // 设置忽略文件，用正则表达式匹配路径\n        }),\n      ]\n    }\n  },\n});\n</code></pre><p></p>', NULL, NULL, 1, 1, 138, 0, 1, 1, '2024-10-21 07:18:06', '2025-11-14 09:19:01', '2025-10-21 07:18:06', '2025-11-14 09:19:01');
INSERT INTO `posts` VALUES (5, '图片热区点击、悬停、离开功能', '<img class=\"editor-image\" src=\"/uploads/general/2025/10/ef910d4cd50de640df9a65ee9215e0f0.png\" width=\"841\" height=\"402\" data-align=\"center\" style=\"width: 841px; height: 402px;\"><h3>如图，需要点击图片中的区域去弹出信息</h3><h3>封装一个公共的组件，将需要用到的区域去遍历到组件里并绑定事件</h3><pre><code class=\"language-javascript\">&lt;template&gt;\n  &lt;div class=\"image-mapper\" ref=\"mapperRef\"&gt;\n    &lt;!-- 底图 --&gt;\n    &lt;img\n      :src=\"props.src\"\n      @load=\"handleImageLoad\"\n      :style=\"imageStyle\"\n      class=\"base-image\"\n    /&gt;\n\n    &lt;!-- 区域层 --&gt;\n    &lt;div class=\"areas-container\" :style=\"containerStyle\"&gt;\n      &lt;template v-for=\"(area, index) in computedAreas\" :key=\"index\"&gt;\n        &lt;!-- 矩形区域 --&gt;\n        &lt;div\n          v-if=\"area.shape === \'rect\'\"\n          class=\"area rectangle\"\n          :style=\"area.style\"\n          @click=\"handleAreaClick(area)\"\n          @mouseover=\"handleAreaHover(area)\"\n          @mouseleave=\"handleAreaLeave(area)\"\n        &gt;\n          &lt;div v-if=\"showTooltip &amp;&amp; hoveredArea === area\" class=\"tooltip\"&gt;\n            {{ area.title }}\n          &lt;/div&gt;\n        &lt;/div&gt;\n\n        &lt;!-- 圆形区域 --&gt;\n        &lt;div\n          v-else-if=\"area.shape === \'circle\'\"\n          class=\"area circle\"\n          :style=\"area.style\"\n          @click=\"handleAreaClick(area)\"\n          @mouseover=\"handleAreaHover(area)\"\n          @mouseleave=\"handleAreaLeave(area)\"\n        &gt;\n          &lt;div v-if=\"showTooltip &amp;&amp; hoveredArea === area\" class=\"tooltip\"&gt;\n            {{ area.title }}\n          &lt;/div&gt;\n        &lt;/div&gt;\n\n        &lt;!-- 多边形区域 --&gt;\n        &lt;div\n          v-else-if=\"area.shape === \'poly\'\"\n          class=\"area polygon\"\n          :style=\"area.style\"\n          @click=\"handleAreaClick(area)\"\n          @mouseover=\"handleAreaHover(area)\"\n          @mouseleave=\"handleAreaLeave(area)\"\n        &gt;\n          &lt;div v-if=\"showTooltip &amp;&amp; hoveredArea === area\" class=\"tooltip\"&gt;\n            {{ area.title }}\n          &lt;/div&gt;\n        &lt;/div&gt;\n      &lt;/template&gt;\n    &lt;/div&gt;\n  &lt;/div&gt;\n&lt;/template&gt;\n\n&lt;script setup&gt;\nimport { ref, computed, onMounted, onUnmounted } from \"vue\";\nimport { useElementSize } from \"@vueuse/core\";\n\nconst props = defineProps({\n  src: {\n    type: String,\n    required: true,\n  },\n  areas: {\n    type: Array,\n    default: () =&gt; [],\n  },\n  responsive: {\n    type: Boolean,\n    default: true,\n  },\n  showTooltip: {\n    type: Boolean,\n    default: true,\n  },\n});\n\nconst emit = defineEmits([\"click\", \"mouseover\", \"mouseleave\"]);\n\nconst mapperRef = ref(null);\nconst { width: containerWidth } = useElementSize(mapperRef);\nconst naturalWidth = ref(0);\nconst naturalHeight = ref(0);\nconst hoveredArea = ref(null);\n\n// 计算缩放比例\nconst scale = computed(() =&gt; {\n  if (!props.responsive || !naturalWidth.value) return 1;\n  return containerWidth.value / naturalWidth.value;\n});\n\n// 图片样式\nconst imageStyle = computed(() =&gt; ({\n  width: props.responsive ? \"100%\" : `${naturalWidth.value}px`,\n  height: props.responsive ? \"auto\" : `${naturalHeight.value}px`,\n}));\n\n// 容器样式\nconst containerStyle = computed(() =&gt; ({\n  position: \"absolute\",\n  top: 0,\n  left: 0,\n  width: props.responsive ? \"100%\" : `${naturalWidth.value}px`,\n  height: props.responsive ? \"auto\" : `${naturalHeight.value}px`,\n}));\n\n// 处理区域样式计算\nconst computedAreas = computed(() =&gt; {\n  return props.areas.map((area) =&gt; {\n    const style = calculateAreaStyle(area);\n    return {\n      ...area,\n      style,\n    };\n  });\n});\n\n// 计算区域样式\nconst calculateAreaStyle = (area) =&gt; {\n  const baseStyle = {\n    position: \"absolute\",\n    cursor: \"pointer\",\n  };\n\n  switch (area.shape) {\n    case \"rect\":\n      const [x, y, width, height] = area.coords;\n      return {\n        ...baseStyle,\n        left: `${x * scale.value}px`,\n        top: `${y * scale.value}px`,\n        width: `${width * scale.value}px`,\n        height: `${height * scale.value}px`,\n      };\n\n    case \"circle\":\n      const [cx, cy, r] = area.coords;\n      return {\n        ...baseStyle,\n        left: `${(cx - r) * scale.value}px`,\n        top: `${(cy - r) * scale.value}px`,\n        width: `${r * 2 * scale.value}px`,\n        height: `${r * 2 * scale.value}px`,\n        borderRadius: \"50%\",\n      };\n\n    case \"poly\":\n      const points = area.coords\n        .map((coord, index) =&gt;\n          index % 2 === 0\n            ? `${coord * scale.value}px`\n            : `${coord * scale.value}px`\n        )\n        .join(\",\");\n      return {\n        ...baseStyle,\n        clipPath: `polygon(${points})`,\n      };\n\n    default:\n      return baseStyle;\n  }\n};\n\n// 图片加载完成处理\nconst handleImageLoad = (e) =&gt; {\n  naturalWidth.value = e.target.naturalWidth;\n  naturalHeight.value = e.target.naturalHeight;\n};\n\n// 区域点击处理\nconst handleAreaClick = (area) =&gt; {\n  emit(\"click\", area);\n};\n\n// 区域悬停处理\nconst handleAreaHover = (area) =&gt; {\n  hoveredArea.value = area;\n  emit(\"mouseover\", area);\n};\n\n// 区域离开处理\nconst handleAreaLeave = (area) =&gt; {\n  hoveredArea.value = null;\n  emit(\"mouseleave\", area);\n};\n\n// 监听窗口大小变化\nconst handleResize = () =&gt; {\n  // 触发重新计算\n};\n\nonMounted(() =&gt; {\n  if (props.responsive) {\n    window.addEventListener(\"resize\", handleResize);\n  }\n});\n\nonUnmounted(() =&gt; {\n  if (props.responsive) {\n    window.removeEventListener(\"resize\", handleResize);\n  }\n});\n&lt;/script&gt;\n\n&lt;style scoped&gt;\n.image-mapper {\n  position: relative;\n  display: inline-block;\n  max-width: 100%;\n}\n\n.base-image {\n  display: block;\n  max-width: 100%;\n  height: auto;\n}\n\n.areas-container {\n  pointer-events: none;\n}\n\n.area {\n  pointer-events: auto;\n  transition: background-color 0.3s;\n  background-color: rgba(255, 255, 255, 0);\n}\n\n.area:hover {\n  background-color: rgba(255, 255, 255, 0.2);\n}\n\n.tooltip {\n  position: absolute;\n  background: rgba(0, 0, 0, 0.8);\n  color: white;\n  padding: 4px 8px;\n  border-radius: 4px;\n  font-size: 12px;\n  white-space: nowrap;\n  top: -30px;\n  left: 50%;\n  transform: translateX(-50%);\n  z-index: 1000;\n}\n&lt;/style&gt;</code></pre><h3>使用示例</h3><pre><code class=\"language-javascript\">&lt;template&gt;\n  &lt;div&gt;\n    &lt;ImageMapper\n      :src=\"getAssetsURL(imageUrl)\"\n      :areas=\"areas\"\n      @click=\"handleAreaClick\"\n      @mouseover=\"handleAreaHover\"\n      @mouseleave=\"handleAreaLeave\"\n    /&gt;\n  &lt;/div&gt;\n&lt;/template&gt;\n\n&lt;script setup&gt;\nimport ImageMapper from \"../utils/ImageMapper.vue\";\nimport { getAssetsURL } from \"@/utils/load_assets\";\n\nconst imageUrl = \"other/about/pc_aboutus_skf.png\";\n\nconst areas = [\n  {\n    id: 1,\n    shape: \"rect\",\n    coords: [286, 40, 648, 192], // [x, y, width, height]\n    title: \"矩形区域123\",\n  },\n  // {\n  //   id: 2,\n  //   shape: \"circle\",\n  //   coords: [200, 200, 50], // [centerX, centerY, radius]\n  //   title: \"圆形区域\",\n  // },\n  // {\n  //   id: 3,\n  //   shape: \"poly\",\n  //   coords: [300, 100, 400, 100, 350, 200], // [x1, y1, x2, y2, x3, y3, ...]\n  //   title: \"多边形区域\",\n  // },\n];\n\nconst handleAreaClick = (area) =&gt; {\n  alert(`点击了区域: ${area.title}`);\n};\n\nconst handleAreaHover = (area) =&gt; {\n  console.log(\"悬停区域:\", area);\n};\n\nconst handleAreaLeave = (area) =&gt; {\n  console.log(\"离开区域:\", area);\n};\n&lt;/script&gt;\n\n&lt;style scoped lang=\"scss\"&gt;\n/**\n* @description: *** 页\n* @author: duyong\n* @Date:\n*/\n@use \"@/assets/css/element-plus.scss\";\n&lt;/style&gt;</code></pre><h3>tips:如果图片是n倍导出，对应x,y,width,height也要*2,因为是基于图片去定位的</h3><p></p>', NULL, NULL, 1, 1, 607, 0, 1, 1, '2023-10-21 07:40:13', '2025-11-11 04:02:58', '2025-10-21 07:40:13', '2025-11-11 04:02:59');
INSERT INTO `posts` VALUES (6, 'H5连续返回重进相机变焦', '<h2>视频流管理</h2><h3>问题背景</h3><blockquote><p>在连续返回和进入界面获取视频流时，可能会遇到设备列表不完整的问题。为确保每次都能获取到所有设备，退出界面时应释放视频流资源。</p></blockquote><h3>视频流释放的重要性</h3><ul><li><p>避免设备占用导致的选择错误（如摄像头微焦镜头）</p></li><li><p>确保下次进入时能重新枚举所有可用设备</p></li></ul><h3>视频流管理的实现</h3><ol><li><p>初始化视频组件</p></li></ol><pre><code class=\"language-javascript\">&lt;video\n  v-show=\"videoStatus\"\n  id=\"video\"\n  object-fit=\"cover\"\n  :enable-progress-gesture=\"false\"\n  :show-center-play-btn=\"false\"\n  autoplay\n  muted\n  :controls=\"false\"\n  :playsinline=\"true\"\n  webkit-playsinline=\"true\"\n  x5-video-player-type=\"h5\"\n  x5-video-player-fullscreen=\"true\"\n  x5-video-orientation=\"portraint\"\n  :\n&gt;\n&lt;/video&gt;</code></pre><ol start=\"2\"><li><p>组件挂载与销毁</p></li></ol><ul><li><p>组件挂载时开始获取设备并初始化视频流</p></li><li><p>组件销毁时释放视频流和资源</p></li></ul><h3>获取摄像头设备</h3><pre><code class=\"language-javascript\">const getDevices = async () =&gt; {\n  // 省略部分代码...\n  // 停止现有的视频流（如果有的话）\n  // 获取设备列表并返回\n};\n</code></pre><h3>视频流处理逻辑</h3><blockquote><p>以下代码块展示了如何处理视频流，包括初始化、播放和停止等操作。</p></blockquote><pre><code class=\"language-javascript\">const startFun = async () =&gt; {\n  // 省略部分代码...\n  // 获取设备、设置约束、获取用户媒体流等\n  // 在获取新流之前确保旧流已停止\n  // 初始化视频元素并播放视频流\n  // 更新视频尺寸和状态\n};\n</code></pre><h3>清理摄像头资源</h3><pre><code class=\"language-javascript\">const clearCamera = () =&gt; {\n  // 更新视频状态\n  videoStatus.value = false;\n\n  // 停止视频流并清除相关资源\n  if (videoStream.value) {\n    videoStream.value.getTracks().forEach(track =&gt; track.stop());\n    videoStream.value = null;\n  }\n\n  // 清除视频元素\n  if (video.value) {\n    video.value.srcObject = null;\n    video.value.load(); // 重置视频元素\n  }\n};\n</code></pre><p>通过以上步骤，可以确保视频流管理的逻辑清晰、资源得到有效释放，进而提升用户体验。</p>', '连续返回进入获取视频流，获取的设备列表会缺少，因此在退出界面时要清除视频流占用，保证下次进入重新获取，后台设备无占用。不然会造成摄像头选择微焦镜头。', NULL, 1, 1, 112, 0, 1, 1, '2025-10-29 01:46:07', '2025-11-10 05:44:15', '2025-10-29 01:46:07', '2025-11-10 05:44:16');
INSERT INTO `posts` VALUES (7, '在React中封装socket.io的实践指南', '<p></p><p>在构建实时通信功能的Web应用时，<a target=\"_blank\" rel=\"noopener noreferrer\" href=\"http://socket.io\">socket.io</a>已成为开发者的首选库之一。React，作为一个用于构建用户界面的JavaScript库，与socket.io的结合可以为用户提供流畅的实时互动体验。以下是如何在React应用中封装并使用socket.io的深度扩展内容。</p><h3>核心概念与背景知识</h3><p>socket.io在客户端和服务器之间提供了一个基于事件的实时双向通信通道。以下是几个关键概念：</p><ul><li><p><strong>事件驱动：</strong>socket.io基于发布/订阅模式，允许任何一端发送或接收自定义事件。</p></li><li><p><strong>实时性：</strong>socket.io使用了WebSocket协议，当条件不允许时，会优雅地降级到长轮询等技术。</p></li><li><p><strong>跨平台：</strong>socket.io支持多种浏览器和移动平台，易于在React Native等环境中使用。</p></li></ul><h3>封装socket.io的步骤</h3><ol><li><p></p></li></ol><h4>安装socket.io-client</h4><p>在React项目的根目录下，使用npm或yarn安装<code>socket.io-client</code>包。</p><pre><code class=\"language-bash\">npm install socket.io-client</code></pre><ul><li><p></p></li></ul><h4>创建Socket组件</h4><p>在React组件中封装socket.io的连接和事件处理逻辑。</p><pre><code class=\"language-typescript\">\nclass SocketComponent extends \nReact.Component {\n  componentDidMount() {\n    this.socket = io(\'http://localhost:3001\');\n    // 绑定事件处理器\n\n    this.socket.on(\'message\', (data) =&gt; {\n      this.props.onMessage(data);\n    });\n  }\n  // 发送事件\n  sendMessage = (message) =&gt; {\n    this.socket.emit(\'message\', message);\n  }\n  render() {\n    return (\n      &lt;div&gt;\n        {this.props.children}\n      &lt;/div&gt;\n    );\n  }\n}\n    </code></pre><ul><li><p></p></li></ul><h4>在React组件中使用Socket组件</h4><p>在其他组件中，我们可以如下使用Socket组件来发送和接收消息。</p><pre><code class=\"language-typescript\">\nclass Chat extends React.Component {\n  state = {\n    messages: []\n  };\n  handleMessage = (message) =&gt; {\n    this.setState((prevState) =&gt; ({\n      messages: [...prevState.messages, message]\n    }));\n  };\n  render() {\n    return (\n      &lt;SocketComponent onMessage={this.handleMessage}&gt;\n        &lt;ChatInterface messages={this.state.messages} sendMessage={this.sendMessage}/&gt;\n      &lt;/SocketComponent&gt;\n    );\n  }\n}\n    </code></pre><h3>案例分析与延伸思考</h3><p>以下是一个具体的案例，展示如何在一个简单的聊天应用中实现React和socket.io的结合。</p><ul><li><p></p></li></ul><h4>案例：实时聊天室</h4><p>在聊天室中，当用户发送消息时，其他所有用户应立即看到该消息。</p><ul><li><p>使用socket.io建立服务器与客户端之间的实时连接。</p></li><li><p>在客户端，创建React组件来展示消息列表，并允许用户发送新消息。</p></li><li><p>当用户发送消息时，通过socket.io触发服务器的\'message\'事件。</p></li><li><p>服务器接收到消息后，广播给所有连接的客户端。</p></li></ul><ul><li><p></p></li></ul><h4>延伸思考</h4><p>在更复杂的实时应用中，我们还需考虑以下问题：</p><ul><li><p><em>连接管理：</em>如何优雅地处理用户断开重连、网络不稳定等情况？</p></li><li><p><em>性能优化：</em>如何减少不必要的事件发送和接收，避免性能瓶颈？</p></li><li><p><em>安全性：</em>如何确保通过socket.io传输的数据是加密和安全的？</p></li></ul><p>通过上述内容，我们可以看到在React中封装并使用socket.io不仅能够提升应用的实时交互能力，而且通过合理的组件封装和事件管理</p>', 'React封装使用socket.io', NULL, 1, 1, 42, 0, 1, 1, '2025-10-30 16:00:50', '2025-11-04 05:22:15', '2025-10-30 16:00:50', '2025-11-04 05:22:15');
INSERT INTO `posts` VALUES (8, '如何使用Canvas实现一个绘制蒙版层的组件，用于调用ComfyUI API时传递蒙版图层', '<h2>ComfyUI 与 Canvas 应用概述</h2><h3>1.1 ComfyUI 的功能与局限</h3><p>ComfyUI 允许用户将工作流导出为 API，便于外部网站调用。然而，在图像处理方面，它需要手动通过软件绘制蒙版，这一过程相对繁琐。为提升效率，我们考虑利用 HTML5 的 <code>canvas</code> 元素来实现一个蒙版绘制工具。</p><h3>1.2 蒙版绘制工具的优势</h3><ul><li><p>简化流程：在填写表单的同时，可以快速绘制蒙版。</p></li><li><p>提高效率：减少在不同软件间切换的时间，提升工作流。</p></li></ul><h2>2. Canvas 的应用</h2><h3>2.1 Canvas 简介</h3><blockquote><p>Canvas 是 HTML5 引入的一个元素，用于在网页上绘制图形和图像。它支持通过 JavaScript 进行动态渲染，非常适合开发交互式图像处理工具。</p></blockquote><h3>2.2 Canvas 实现蒙版绘制工具的原理</h3><ol><li><p>利用 Canvas API 创建一个画布。</p></li><li><p>通过用户交互（如鼠标或触摸事件）在画布上绘制图形。</p></li><li><p>将绘制的图形转换为蒙版，用于图像处理。</p></li></ol><h3>2.3 Canvas 的兼容性与拓展性</h3><p>Canvas 元素在现代浏览器上得到了广泛支持，可以用于实现各种复杂的图像处理功能。此外，其与 JavaScript 的良好结合，为开发更多交互式应用提供了可能。</p><h2>3. 示例</h2><h3>3.1 Vue组件</h3><pre><code class=\"language-javascript\">&lt;template&gt;\n  &lt;div class=\"image-mask-drawer\"&gt;\n    &lt;!-- 工具栏 --&gt;\n    &lt;div\n      class=\"toolbar flex items-center gap-3 mb-4 p-3 bg-gray-50 rounded-lg flex-wrap\"\n    &gt;\n      &lt;!-- 画笔形状 --&gt;\n      &lt;div class=\"flex items-center gap-2\"&gt;\n        &lt;label class=\"text-sm text-gray-700\"&gt;画笔形状:&lt;/label&gt;\n        &lt;div class=\"flex gap-1 bg-white rounded-md p-1 border border-gray-200\"&gt;\n          &lt;button\n            class=\"px-3 py-1 text-xs rounded transition-colors\"\n            :class=\"\n              brushShape === \'circle\'\n                ? \'bg-blue-600 text-white\'\n                : \'text-gray-600 hover:bg-gray-100\'\n            \"\n            @click=\"brushShape = \'circle\'\"\n          &gt;\n            ⚫ 圆形\n          &lt;/button&gt;\n          &lt;button\n            class=\"px-3 py-1 text-xs rounded transition-colors\"\n            :class=\"\n              brushShape === \'rect\'\n                ? \'bg-blue-600 text-white\'\n                : \'text-gray-600 hover:bg-gray-100\'\n            \"\n            @click=\"brushShape = \'rect\'\"\n          &gt;\n            ⬛ 矩形\n          &lt;/button&gt;\n        &lt;/div&gt;\n      &lt;/div&gt;\n\n      &lt;div class=\"h-6 w-px bg-gray-300\"&gt;&lt;/div&gt;\n\n      &lt;!-- 画笔大小 --&gt;\n      &lt;div class=\"flex items-center gap-2\"&gt;\n        &lt;label class=\"text-sm text-gray-700\"&gt;画笔大小:&lt;/label&gt;\n        &lt;input\n          v-model.number=\"brushSize\"\n          type=\"range\"\n          min=\"5\"\n          max=\"80\"\n          class=\"w-32\"\n        /&gt;\n        &lt;span class=\"text-sm text-gray-600\"&gt;{{ brushSize }}px&lt;/span&gt;\n      &lt;/div&gt;\n\n      &lt;div class=\"h-6 w-px bg-gray-300\"&gt;&lt;/div&gt;\n\n      &lt;button\n        class=\"px-3 py-1.5 text-sm bg-blue-600 text-white rounded hover:bg-blue-700 transition-colors\"\n        @click=\"clearMask\"\n      &gt;\n        清除遮罩\n      &lt;/button&gt;\n\n      &lt;button\n        class=\"px-3 py-1.5 text-sm bg-green-600 text-white rounded hover:bg-green-700 transition-colors\"\n        @click=\"confirmMask\"\n      &gt;\n        确认遮罩\n      &lt;/button&gt;\n\n      &lt;button\n        class=\"px-3 py-1.5 text-sm bg-gray-600 text-white rounded hover:bg-gray-700 transition-colors\"\n        @click=\"cancelEdit\"\n      &gt;\n        取消\n      &lt;/button&gt;\n\n      &lt;div class=\"ml-auto text-xs text-gray-500\"&gt;\n        💡 在图片上涂抹需要AI生成替换的区域\n      &lt;/div&gt;\n    &lt;/div&gt;\n\n    &lt;!-- Canvas 容器 --&gt;\n    &lt;div\n      class=\"canvas-container relative border-2 border-gray-300 rounded-lg overflow-hidden bg-gray-100\"\n    &gt;\n      &lt;canvas\n        ref=\"canvasRef\"\n        class=\"cursor-none\"\n        @mousedown=\"startDrawing\"\n        @mousemove=\"handleMouseMove\"\n        @mouseup=\"stopDrawing\"\n        @mouseleave=\"handleMouseLeave\"\n        @touchstart.prevent=\"handleTouchStart\"\n        @touchmove.prevent=\"handleTouchMove\"\n        @touchend.prevent=\"stopDrawing\"\n      &gt;&lt;/canvas&gt;\n\n      &lt;!-- 自定义光标预览 --&gt;\n      &lt;div\n        v-if=\"showCursor\"\n        class=\"brush-cursor pointer-events-none absolute border-2 border-blue-500 opacity-60\"\n        :class=\"brushShape === \'circle\' ? \'rounded-full\' : \'rounded-sm\'\"\n        :style=\"{\n          left: cursorPos.x + \'px\',\n          top: cursorPos.y + \'px\',\n          width: brushSize + \'px\',\n          height: brushSize + \'px\',\n          transform: \'translate(-50%, -50%)\',\n          backgroundColor: \'rgba(0, 0, 0, 0.2)\',\n        }\"\n      &gt;&lt;/div&gt;\n    &lt;/div&gt;\n\n    &lt;!-- 提示信息 --&gt;\n    &lt;div class=\"mt-3 text-xs text-gray-500\"&gt;\n      &lt;p&gt;• 用鼠标或手指在图片上涂抹，标记需要AI生成替换的区域&lt;/p&gt;\n      &lt;p&gt;• 涂抹的区域（黑色半透明）将被AI生成的新内容替换&lt;/p&gt;\n      &lt;p&gt;• 未涂抹的区域将保持原图不变&lt;/p&gt;\n    &lt;/div&gt;\n  &lt;/div&gt;\n&lt;/template&gt;\n\n&lt;script setup&gt;\nimport { ref, onMounted, watch } from \"vue\";\nimport { getProxiedImageUrl } from \"@/utils/image\";\n\nconst props = defineProps({\n  imageUrl: {\n    type: String,\n    required: true,\n  },\n  maxWidth: {\n    type: Number,\n    default: 800,\n  },\n  maxHeight: {\n    type: Number,\n    default: 600,\n  },\n});\n\nconst emit = defineEmits([\"confirm\", \"cancel\"]);\n\nconst canvasRef = ref(null);\nconst brushSize = ref(20);\nconst brushShape = ref(\"circle\"); // \'circle\' | \'rect\'\nconst isDrawing = ref(false);\nconst showCursor = ref(false);\nconst cursorPos = ref({ x: 0, y: 0 });\n\nlet ctx = null;\nlet image = null;\nlet maskCanvas = null;\nlet maskCtx = null;\nlet lastX = null;\nlet lastY = null;\n\n// 性能优化：使用 requestAnimationFrame\nlet rafId = null;\nlet pendingDraws = [];\n\nonMounted(() =&gt; {\n  initCanvas();\n});\n\nwatch(\n  () =&gt; props.imageUrl,\n  () =&gt; {\n    initCanvas();\n  }\n);\n\nconst initCanvas = async () =&gt; {\n  if (!props.imageUrl || !canvasRef.value) return;\n\n  const canvas = canvasRef.value;\n  ctx = canvas.getContext(\"2d\");\n\n  // 加载图片\n  image = new Image();\n  image.crossOrigin = \"anonymous\";\n\n  // 处理图片URL：如果是外部URL，通过后端代理\n  const imageUrl = getProxiedImageUrl(props.imageUrl, { log: true });\n\n  image.onload = () =&gt; {\n    // 计算缩放后的尺寸，保持比例\n    let width = image.width;\n    let height = image.height;\n\n    const widthRatio = props.maxWidth / width;\n    const heightRatio = props.maxHeight / height;\n    const ratio = Math.min(widthRatio, heightRatio);\n\n    width = Math.floor(width * ratio);\n    height = Math.floor(height * ratio);\n\n    // 设置canvas尺寸\n    canvas.width = width;\n    canvas.height = height;\n\n    // 创建遮罩层canvas\n    maskCanvas = document.createElement(\"canvas\");\n    maskCanvas.width = width;\n    maskCanvas.height = height;\n    maskCtx = maskCanvas.getContext(\"2d\");\n\n    // 绘制初始图片\n    redraw();\n  };\n\n  image.onerror = () =&gt; {\n    console.error(\"图片加载失败:\", props.imageUrl);\n  };\n\n  image.src = imageUrl;\n};\n\nconst redraw = () =&gt; {\n  if (!ctx || !image) return;\n\n  // 清空画布\n  ctx.clearRect(0, 0, canvasRef.value.width, canvasRef.value.height);\n\n  // 绘制原图\n  ctx.drawImage(image, 0, 0, canvasRef.value.width, canvasRef.value.height);\n\n  // 绘制遮罩层（黑色，不透明度0.8）\n  if (maskCanvas) {\n    ctx.globalAlpha = 0.8;\n    ctx.drawImage(maskCanvas, 0, 0);\n    ctx.globalAlpha = 1.0;\n  }\n};\n\n// 优化的重绘函数（使用 RAF）\nconst scheduleRedraw = () =&gt; {\n  if (rafId) return;\n\n  rafId = requestAnimationFrame(() =&gt; {\n    redraw();\n    rafId = null;\n  });\n};\n\nconst getMousePos = (e) =&gt; {\n  const rect = canvasRef.value.getBoundingClientRect();\n  const scaleX = canvasRef.value.width / rect.width;\n  const scaleY = canvasRef.value.height / rect.height;\n\n  return {\n    x: (e.clientX - rect.left) * scaleX,\n    y: (e.clientY - rect.top) * scaleY,\n  };\n};\n\nconst getTouchPos = (e) =&gt; {\n  const rect = canvasRef.value.getBoundingClientRect();\n  const touch = e.touches[0];\n  const scaleX = canvasRef.value.width / rect.width;\n  const scaleY = canvasRef.value.height / rect.height;\n\n  return {\n    x: (touch.clientX - rect.left) * scaleX,\n    y: (touch.clientY - rect.top) * scaleY,\n  };\n};\n\nconst startDrawing = (e) =&gt; {\n  isDrawing.value = true;\n  const pos = getMousePos(e);\n  lastX = pos.x;\n  lastY = pos.y;\n  drawBrush(pos.x, pos.y);\n};\n\nconst handleMouseMove = (e) =&gt; {\n  // 更新光标位置\n  const rect = canvasRef.value.getBoundingClientRect();\n  cursorPos.value = {\n    x: e.clientX - rect.left,\n    y: e.clientY - rect.top,\n  };\n  showCursor.value = true;\n\n  // 如果正在绘制，执行绘制操作\n  if (isDrawing.value) {\n    const pos = getMousePos(e);\n    interpolateDraw(lastX, lastY, pos.x, pos.y);\n    lastX = pos.x;\n    lastY = pos.y;\n  }\n};\n\nconst handleMouseLeave = () =&gt; {\n  showCursor.value = false;\n  stopDrawing();\n};\n\nconst stopDrawing = () =&gt; {\n  isDrawing.value = false;\n  lastX = null;\n  lastY = null;\n\n  // 最后绘制一次确保所有内容都显示\n  redraw();\n};\n\nconst handleTouchStart = (e) =&gt; {\n  isDrawing.value = true;\n  const pos = getTouchPos(e);\n  lastX = pos.x;\n  lastY = pos.y;\n  drawBrush(pos.x, pos.y);\n};\n\nconst handleTouchMove = (e) =&gt; {\n  if (!isDrawing.value) return;\n  const pos = getTouchPos(e);\n\n  // 插值绘制\n  interpolateDraw(lastX, lastY, pos.x, pos.y);\n\n  lastX = pos.x;\n  lastY = pos.y;\n};\n\n// 插值绘制，填补快速移动时的空隙\nconst interpolateDraw = (x0, y0, x1, y1) =&gt; {\n  const dist = Math.sqrt((x1 - x0) ** 2 + (y1 - y0) ** 2);\n  const steps = Math.max(Math.floor(dist / (brushSize.value / 4)), 1);\n\n  for (let i = 0; i &lt;= steps; i++) {\n    const t = i / steps;\n    const x = x0 + (x1 - x0) * t;\n    const y = y0 + (y1 - y0) * t;\n    drawBrushDirect(x, y);\n  }\n\n  scheduleRedraw();\n};\n\n// 直接绘制到遮罩层（不立即重绘整个canvas）\nconst drawBrushDirect = (x, y) =&gt; {\n  if (!maskCtx) return;\n\n  // 黑色遮罩\n  maskCtx.fillStyle = \"rgba(0, 0, 0, 1)\";\n\n  if (brushShape.value === \"circle\") {\n    // 圆形画笔\n    maskCtx.beginPath();\n    maskCtx.arc(x, y, brushSize.value / 2, 0, Math.PI * 2);\n    maskCtx.fill();\n  } else {\n    // 矩形画笔\n    const halfSize = brushSize.value / 2;\n    maskCtx.fillRect(\n      x - halfSize,\n      y - halfSize,\n      brushSize.value,\n      brushSize.value\n    );\n  }\n};\n\n// 单次绘制（用于点击）\nconst drawBrush = (x, y) =&gt; {\n  drawBrushDirect(x, y);\n  scheduleRedraw();\n};\n\nconst clearMask = () =&gt; {\n  if (!maskCtx) return;\n  maskCtx.clearRect(0, 0, maskCanvas.width, maskCanvas.height);\n  redraw();\n};\n\nconst confirmMask = () =&gt; {\n  // 创建遮罩图：原图 + alpha通道（ComfyUI格式）\n  // RGB通道保留原图，alpha通道表示遮罩（0=透明/需要替换，255=不透明/保留）\n  const resultCanvas = document.createElement(\"canvas\");\n  resultCanvas.width = maskCanvas.width;\n  resultCanvas.height = maskCanvas.height;\n  const resultCtx = resultCanvas.getContext(\"2d\");\n\n  // 1. 先绘制原始场景图作为底图\n  resultCtx.drawImage(image, 0, 0, resultCanvas.width, resultCanvas.height);\n\n  // 2. 获取遮罩层的像素数据\n  const maskImageData = maskCtx.getImageData(\n    0,\n    0,\n    maskCanvas.width,\n    maskCanvas.height\n  );\n  const maskData = maskImageData.data;\n\n  // 3. 获取结果画布的像素数据\n  const resultImageData = resultCtx.getImageData(\n    0,\n    0,\n    resultCanvas.width,\n    resultCanvas.height\n  );\n  const resultData = resultImageData.data;\n\n  // 4. 修改alpha通道：涂抹的区域设为透明（alpha=0，表示\"把产品放在这里\"）\n  for (let i = 0; i &lt; maskData.length; i += 4) {\n    // 如果遮罩层有内容（用户涂抹的区域）\n    if (maskData[i + 3] &gt; 0) {\n      // 将alpha通道设为0（透明），RGB保持原图\n      resultData[i + 3] = 0; // Alpha = 0 表示需要替换的区域\n    }\n    // 其他区域的alpha保持255（不透明），表示保留原图\n  }\n\n  // 5. 将处理后的数据写回画布\n  resultCtx.putImageData(resultImageData, 0, 0);\n\n  // 6. 生成最终的遮罩图（原图 + alpha通道遮罩）\n  const maskDataUrl = resultCanvas.toDataURL(\"image/png\");\n\n  emit(\"confirm\", {\n    mask: maskDataUrl,\n    width: resultCanvas.width,\n    height: resultCanvas.height,\n  });\n};\n\nconst cancelEdit = () =&gt; {\n  emit(\"cancel\");\n};\n&lt;/script&gt;\n\n&lt;style scoped&gt;\n.canvas-container {\n  display: inline-block;\n  max-width: 100%;\n  position: relative;\n}\n\ncanvas {\n  display: block;\n  max-width: 100%;\n  height: auto;\n}\n\n.brush-cursor {\n  transition: width 0.1s ease, height 0.1s ease;\n  z-index: 10;\n}\n\ninput[type=\"range\"] {\n  cursor: pointer;\n}\n\n/* 隐藏默认光标 */\n.cursor-none {\n  cursor: none;\n}\n&lt;/style&gt;\n</code></pre><h3>3.2 效果</h3><img class=\"editor-image\" src=\"/uploads/general/2025/11/6a636723d9e5def6ccb48ea606769ea2.png\" width=\"321\" height=\"381\" data-align=\"left\" style=\"width: 321px; height: 381px;\"><img class=\"editor-image\" src=\"/uploads/general/2025/11/9d6b6dee452301c58b850fad7fad4796.png\" width=\"320\" height=\"332\" data-align=\"left\" style=\"width: 320px; height: 332px;\"><p></p>', '使用Canvas实现一个绘制蒙版层的组件，用于调用ComfyUI API时传递蒙版图层', NULL, 1, 1, 76, 0, 1, 1, '2025-11-03 02:36:49', '2025-11-14 21:54:01', '2025-11-03 02:36:49', '2025-11-14 21:54:01');
INSERT INTO `posts` VALUES (9, '全栈开发通关秘籍：不啃硬书，玩着玩着就会了！', '<p>咱先掰扯清楚：全栈开发者不是 “啥都会点但啥都不精” 的半吊子，而是 “能自己从 0 到 1 搞出个小网站” 的狠人 —— 比如你想做个 “追星相册”，前端能划着看照片，后端能存图不丢，最后还能挂网上让好兄弟一起玩。这事儿听着酷，其实不用抱着厚书死磕，咱一步一步 “玩着学”！​</p><h3><strong>一、先搞懂：全栈不是 “全能超人”，是 “能串起整条线” 的人​</strong></h3><p>别被 “全栈” 俩字吓着！合格的全栈 er 就像 “奶茶店全能店员”：能点单（对接用户需求）、能做茶（写前端界面）、能囤料（搭后端存数据）、还能打包送出去（部署上线）—— 不用每步都做到 “行业 TOP”，但得能把 “点单到送茶” 的流程走通！​</p><p>避坑提醒：别学 “猴子掰玉米”！今天学 React，明天学 Java，后天又搞 Python，最后啥都没记住。先攥紧 1 套 “核心技能”，比如 “前端 Vue + 后端 Node.js”，玩熟了再解锁新技能～​</p><h3><strong>二、闯关模式：3 步从 “小白” 到 “能上线”​</strong></h3><p>全栈学习就像玩闯关游戏，先过第一关攒经验，再打下一关，稳得很！​</p><h4><strong>第一关：先搞定 “半边天”（3-6 个月）—— 选前端或后端，玩出成就感！​</strong></h4><p>推荐零基础先冲<strong>前端</strong>！毕竟写两行代码就能看到 “页面变样”，比对着黑屏写后端爽多了～​</p><p><strong>要是选前端：先玩会 “网页三件套”​</strong></p><ol><li><p><strong>HTML：给网页搭骨架​</strong></p></li></ol><p>不用记所有标签！先搞懂 “语义化”—— 比如用&lt;header&gt;当网页头，&lt;article&gt;装正文，就像给房间分 “客厅、卧室”，别全用&lt;div&gt;堆。​</p><blockquote><p><em>小玩法：给自己写个 “个人简介页”，放张自拍、写两句爱好，成就感拉满～</em>​</p></blockquote><ol start=\"2\"><li><p><strong>CSS：给骨架穿漂亮衣服​</strong></p></li></ol><p>重点玩会 Flex/Grid！用 Flex 让按钮 “居中”，用 Grid 把图片排成 “九宫格”，比瞎调margin快 10 倍。还能玩 CSS 动画 —— 让按钮 hover 时 “跳一下”，超有意思～​</p><blockquote><p><em>推荐工具：Can I Use（查 CSS 属性兼容性）、Codepen（看别人的炫酷效果，抄作业改改）</em>​</p></blockquote><ol start=\"3\"><li><p><strong>JavaScript：给衣服加 “魔法”​</strong></p></li></ol><p>不用死磕闭包、原型链！先学 “能让页面动起来” 的本事：比如点击按钮 “显示 / 隐藏自拍”，输入文字 “实时显示字数”。ES6 + 里先吃透Promise（解决 “回调地狱”，比如加载图片完再显示），够用了～​</p><blockquote><p><em>学习资源：B 站 “技术胖”（讲得像说相声）、MDN 的 “JavaScript 指南”（看不懂就跳，先玩例子）</em>​</p></blockquote><ol start=\"4\"><li><p><strong>框架选 1 个玩熟：Vue 或 React​</strong></p></li></ol><p>Vue 像 “傻瓜相机”，文档写得跟 “说明书” 似的，新手跟着敲 “ Todo 列表”（记待办事项），1 小时就能跑通；React 像 “单反相机”，稍微复杂点，但玩熟了能搞出更酷的效果（比如朋友圈那种下拉刷新）。​</p><blockquote><p><em>实战小目标：搞个 “追星相册前端”—— 能划着看爱豆照片，点照片看大图，搞定！</em>​</p></blockquote><p><strong>要是选后端：先玩会 “存数据 + 返数据”​</strong></p><p>选语言就像选奶茶：​</p><ul><li><p>想跟前端 “套近乎”：选 Node.js（都用 JS，不用换脑子），框架玩 Express—— 写个 “接收表单数据” 的接口，10 行代码搞定；​</p></li></ul><ul><li><p>想稳一点：选 Java（像经典美式，企业都爱用），玩 Spring Boot—— 跟着教程搞个 “用户登录”，输入账号密码能验证；​</p></li></ul><ul><li><p>想快一点：选 Python（像果茶，清爽好上手），玩 Flask—— 写个 “返回随机笑话” 的接口，5 分钟跑通。​</p></li></ul><p>数据库先玩 MySQL！不用学复杂的 “事务”“索引”，先搞懂 “增删改查”—— 比如存 “用户账号密码”，查 “谁登录了”，够用了～​</p><blockquote><p><em>实战小目标：搞个 “随机语录接口”—— 前端调用一下，能返回一句 “毒鸡汤”，成就感爆棚！</em>​</p></blockquote><h4><strong>第二关：打通 “前后端”（2-3 个月）—— 让它们 “聊起来”！​</strong></h4><p>第一关玩熟了，就该让前端和后端 “对话” 了！比如你前端搞了个 “登录框”，输入账号密码，得让后端 “验证对不对”，这步超关键～​</p><ol><li><p><strong>先搞懂 “HTTP 协议”：前后端的 “聊天方言”​</strong></p></li></ol><p>不用背所有状态码！记住 3 个就行：200（聊天成功）、404（后端没找到你要的东西）、500（后端自己崩了）。请求方法就记 GET（要数据，比如要语录）和 POST（发数据，比如发登录信息）。​</p><ol start=\"2\"><li><p><strong>解决 “跨域”：别让浏览器 “拦着聊天”​</strong></p></li></ol><p>前端调后端接口时，浏览器可能会蹦出 “跨域错误”，像保安拦着不让进门。简单招：后端加一句 “允许跨域” 的代码（比如 Node.js 里用cors插件），或者前端用 “代理”（Vue 里改个配置就行），分分钟搞定～​</p><ol start=\"3\"><li><p><strong>实战大任务：搞个 “摸鱼任务板”！​</strong></p></li></ol><p>这项目超实用，还能串起所有技能：​</p><ul><li><p>前端：用 Vue/React 做个页面，能加 “待办事项”（比如 “3 点摸鱼看剧”）、勾掉 “已完成”；​</p></li></ul><ul><li><p>后端：用 Node.js/Flask 写接口，接收前端的 “加任务”“改状态” 请求，存到 MySQL 里；​</p></li></ul><ul><li><p>关键：前端点 “加任务”，后端存好数据，再返回给前端显示 —— 这就通了！​</p></li></ul><blockquote><p><em>玩的时候可以加小彩蛋：比如完成 3 个任务，弹个 “摸鱼达人” 的弹窗～</em>​</p></blockquote><h4><strong>第三关：搞上线！让别人也能玩（1-2 个月）​</strong></h4><p>自己玩得爽不算，得让朋友也能打开你的 “摸鱼任务板”，这步超有成就感！​</p><ol><li><p><strong>Git：给代码整个 “时光机”​</strong></p></li></ol><p>写代码怕删错？用 Git！提交代码时写句备注（比如 “加了弹窗彩蛋”），删错了能回滚到之前的版本。远程仓库用 GitHub，把代码传上去，像存到 “云端硬盘”。​</p><blockquote><p><em>小技巧：记 3 个命令就行 ——git add .（选要提交的代码）、git commit -m \"备注\"（写备注）、git push（传云端）</em>​</p></blockquote><ol start=\"2\"><li><p><strong>部署：把项目 “搬上互联网”​</strong></p></li></ol><p>不用买贵服务器！新手推荐这俩：​</p><ul><li><p>前端静态页：Vercel（连 GitHub，push 代码自动部署，免费！）；​</p></li></ul><ul><li><p>全栈项目：Railway（上传代码就能跑，支持 Node.js/Python，免费额度够玩）；​</p></li></ul><p>要是想折腾，买个阿里云 ECS（学生机超便宜），学两句 Linux 命令（cd进文件夹、ls看文件），手动传代码也能成～​</p><ol start=\"3\"><li><p><strong>最后一步：搞个 “个性域名”​</strong></p></li></ol><p>部署完会有个 “随机域名”（比如 <a target=\"_blank\" rel=\"noopener noreferrer\" href=\"http://xxx.vercel.app\">xxx.vercel.app</a>），想换个酷的？去阿里云买个域名（比如 “<a target=\"_blank\" rel=\"noopener noreferrer\" href=\"http://moyu-task.top\">moyu-task.top</a>”，一年几十块），解析到你的项目，别人输域名就能玩你的 “摸鱼任务板” 啦！​</p><h3><strong>三、进阶：玩点 “高级活”（ optional ）​</strong></h3><p>要是觉得 “通关” 太简单，再解锁点新技能：​</p><ul><li><p>前端玩 SSR：用 Next.js/Nuxt.js 让页面加载更快，还能 SEO（比如 “摸鱼任务板” 能被百度搜到）；​</p></li></ul><ul><li><p>后端玩 Redis：给 “摸鱼任务” 加缓存，别人加载更快；​</p></li></ul><ul><li><p>跨平台：学 React Native，用前端代码写个 “摸鱼任务板” APP，手机也能玩～​</p></li></ul><h3><strong>四、避坑指南：别让 “挫败感” 劝退你！​</strong></h3><ol><li><p><strong>别死磕 “看不懂的知识点”​</strong></p></li></ol><p>比如学 JS 时卡 “闭包”，先跳过！等你写多了 “点击按钮改数据” 的代码，回头再看闭包，可能突然就懂了 —— 技术这东西，越玩越透～​</p><ol start=\"2\"><li><p><strong>多 “抄作业”，少 “空想”​</strong></p></li></ol><p>不会写 “登录功能”？去 GitHub 搜 “Vue 登录示例”，下载下来改改（把别人的 “用户名密码验证” 改成自己的），比自己瞎琢磨快 10 倍。​</p><ol start=\"3\"><li><p><strong>别怕 “bug”，bug 是 “经验包”​</strong></p></li></ol><p>写代码遇到 bug 很正常！比如 “前端调接口没反应”，先看控制台报错，再去 Google 搜 “Axios 没反应 解决”，慢慢就会 “猜 bug 在哪”—— 等你能快速修 bug，就离全栈不远了！​</p><p><strong>结语：全栈是 “玩出来的”，不是 “啃书啃出来的”​</strong></p><p>别把全栈想成 “高大上的技术活”，其实就是 “自己搞个小项目，从界面到上线，全流程玩一遍”。比如你想做个 “猫咪相册”，就从前端搭页面开始，再写后端存照片，最后部署上线 —— 玩着玩着，你就会发现：哦？原来我已经是全栈了！​</p><p>现在就动手：打开 VS Code，写第一行 HTML（<code>&lt;h1&gt;我的全栈之旅&lt;/h1&gt;</code>），通关开始！​</p>', '全栈开发通关秘籍：不啃硬书，玩着玩着就会了！', NULL, 1, 1, 161, 0, 1, 1, '2025-11-04 05:28:01', '2025-11-15 16:51:18', '2025-11-04 05:28:01', '2025-11-15 16:51:19');
INSERT INTO `posts` VALUES (11, '如何使用Cloudflared内网穿透实现公网访问', '<h3>一、下载与安装Cloudflared</h3><p>Cloudflared是Cloudflare提供的一款开源命令行工具，它能够通过Cloudflare的边缘网络创建隧道，将本地服务器安全地暴露到公网上。以下是下载和安装Cloudflared的步骤：</p><pre><code class=\"language-bash\">## 下载安装包\nwget https://ghfast.top/https://github.com/cloudflare/cloudflared/releases/latest/download/cloudflared-linux-amd64.deb\n\n## 安装\ndpkg -i cloudflared-linux-amd64.deb\n\n## 查看是否安装成功\ncloudflared -v</code></pre><p>上述命令通过<code>wget</code>下载了适用于Linux系统的Cloudflared安装包，并通过<code>dpkg</code>命令进行安装。检查安装成功后，用户即可开始配置隧道。</p><h3>二、安装网络工具</h3><p>为了管理和监控网络服务，可能需要安装额外的网络工具：</p><pre><code class=\"language-bash\">netstat -ntlp\n\nsudo apt update # 更新软件包列表\n\nsudo apt install net-tools # 安装 net-tools 工具包</code></pre><p><code>netstat</code>命令用于显示网络连接、路由表、接口统计等网络信息。若需要更详细地了解网络状态，<code>net-tools</code>是一个包含多个网络管理工具的软件包，如<code>ifconfig</code>。</p><h3>三、配置Cloudflared隧道</h3><p>安装完成后，可以通过以下命令创建一个到本地服务器的隧道：</p><pre><code class=\"language-bash\">cloudflared tunnel --url http://127.0.0.1:8434</code></pre><p>此命令指示<em>cloudflared</em>监听本地服务器的8434端口，并通过Cloudflare的边缘网络暴露该服务。以下是对该过程的详细解释：</p><ul><li><p><strong>本地服务器地址：</strong><code>http://127.0.0.1:8434</code> 表示本地服务器运行在8434端口上。</p></li><li><p><strong>Cloudflare隧道：</strong> 通过<code>--url</code>参数设置，Cloudflared会将所有到Cloudflare边缘节点的请求转发到这个地址。</p></li></ul><h3>四、背景知识与案例</h3><p>Cloudflare的隧道代理技术常用于以下场景：</p><ul><li><p><strong>远程访问：</strong> 开发者可以在家中安全地访问办公室内网的服务器。</p></li><li><p><strong>服务暴露：</strong> 对于没有公网IP的本地服务，可以通过Cloudflared快速地将其暴露到公网，接受外部请求。</p></li><li><p><strong>全球加速：</strong> 对于需要高可用性的服务，Cloudflare的全球边缘网络可以提供内容分发和负载均衡，提高访问速度。</p></li></ul><h3>五、延伸思考</h3><p>在使用Cloudflared时，应当注意以下几点：</p><ol><li><p><strong>安全性：</strong> 隧道通信使用了Cloudflare的加密技术，确保数据传输安全。</p></li><li><p><strong>性能考量：</strong> 考虑到网络延迟和带宽限制，应合理评估使用隧道代理对服务性能的影响。</p></li><li><p><strong>服务监控：</strong> 配置相应的监控工具，实时检查隧道状态和服务可用性。</p></li></ol><p>通过以上步骤，用户可以有效地利用Cloudflared为本地服务提供安全、高效的公网访问能力。</p>', 'Cloudflared内网穿透实现公网访问', NULL, 1, 1, 45, 0, 1, 1, '2025-11-12 13:15:27', '2025-11-16 10:07:50', '2025-11-12 13:15:27', '2025-11-16 10:07:50');

-- ----------------------------
-- Table structure for projects
-- ----------------------------
DROP TABLE IF EXISTS `projects`;
CREATE TABLE `projects`  (
  `id` int NOT NULL AUTO_INCREMENT COMMENT '项目ID',
  `title` varchar(200) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '项目标题',
  `slug` varchar(200) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT 'URL友好标识',
  `description` text CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL COMMENT '项目简介',
  `content` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL COMMENT '项目详细介绍(支持Markdown)',
  `cover_image` varchar(500) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '项目封面图',
  `icon` varchar(500) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '项目图标',
  `status` enum('active','archived','developing','paused') CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT 'developing' COMMENT '项目状态',
  `visibility` enum('public','private') CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT 'public' COMMENT '可见性',
  `language` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '主要编程语言',
  `language_color` varchar(20) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '语言颜色(十六进制)',
  `tags` json NULL COMMENT '项目标签数组',
  `tech_stack` json NULL COMMENT '技术栈数组',
  `features` json NULL COMMENT '项目特性数组',
  `github_url` varchar(500) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT 'GitHub仓库地址',
  `gitee_url` varchar(500) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT 'Gitee仓库地址',
  `demo_url` varchar(500) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '在线演示地址',
  `docs_url` varchar(500) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '文档地址',
  `npm_package` varchar(200) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT 'NPM包名',
  `stars` int NULL DEFAULT 0 COMMENT 'Star数量',
  `forks` int NULL DEFAULT 0 COMMENT 'Fork数量',
  `watchers` int NULL DEFAULT 0 COMMENT 'Watch数量',
  `issues` int NULL DEFAULT 0 COMMENT '问题数量',
  `downloads` int NULL DEFAULT 0 COMMENT '下载量(NPM等)',
  `is_featured` tinyint(1) NOT NULL DEFAULT 0 COMMENT '是否精选展示',
  `is_open_source` tinyint(1) NOT NULL DEFAULT 1 COMMENT '是否开源',
  `display_order` int NULL DEFAULT 0 COMMENT '显示排序(数值越大越靠前)',
  `author_id` int NOT NULL COMMENT '创建者ID(管理员)',
  `view_count` int NULL DEFAULT 0 COMMENT '浏览次数',
  `started_at` date NULL DEFAULT NULL COMMENT '项目开始日期',
  `last_updated_at` datetime NULL DEFAULT NULL COMMENT '最后更新时间',
  `created_at` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `updated_at` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '更新时间',
  PRIMARY KEY (`id`) USING BTREE,
  UNIQUE INDEX `uk_slug`(`slug` ASC) USING BTREE,
  INDEX `idx_status`(`status` ASC) USING BTREE,
  INDEX `idx_visibility`(`visibility` ASC) USING BTREE,
  INDEX `idx_is_featured`(`is_featured` ASC) USING BTREE,
  INDEX `idx_display_order`(`display_order` DESC) USING BTREE,
  INDEX `idx_created_at`(`created_at` DESC) USING BTREE,
  INDEX `idx_author_id`(`author_id` ASC) USING BTREE,
  FULLTEXT INDEX `ft_search`(`title`, `description`),
  CONSTRAINT `fk_projects_author_id` FOREIGN KEY (`author_id`) REFERENCES `users` (`id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE = InnoDB AUTO_INCREMENT = 53 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_unicode_ci COMMENT = '项目表' ROW_FORMAT = Dynamic;

-- ----------------------------
-- Records of projects
