-- ----------------------------
-- Table structure for user_activities
-- ----------------------------
DROP TABLE IF EXISTS `user_activities`;
CREATE TABLE `user_activities`  (
  `id` int NOT NULL AUTO_INCREMENT COMMENT '活动ID',
  `user_id` int NOT NULL COMMENT '用户ID',
  `type` enum('post_created','post_updated','post_deleted','note_created','note_updated','note_deleted','comment_created','comment_updated','comment_deleted','post_liked','post_unliked','note_liked','note_unliked','post_bookmarked','post_unbookmarked','like_received','comment_received','bookmark_received','achievement_unlocked','level_up','milestone_reached','post_approved','post_rejected','comment_approved','comment_rejected','system_notice','account_warning','welcome','post_trending','post_featured') CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '活动类型',
  `title` varchar(200) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '活动标题',
  `description` text CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL COMMENT '活动描述',
  `link` varchar(500) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '相关链接',
  `metadata` json NULL COMMENT '额外元数据',
  `priority` tinyint NOT NULL DEFAULT 5 COMMENT '优先级(0-10，数值越大优先级越高)',
  `is_read` tinyint(1) NOT NULL DEFAULT 0 COMMENT '是否已读(0=未读, 1=已读)',
  `created_at` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `updated_at` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '更新时间',
  PRIMARY KEY (`id`) USING BTREE,
  INDEX `idx_user_id`(`user_id` ASC) USING BTREE,
  INDEX `idx_type`(`type` ASC) USING BTREE,
  INDEX `idx_priority`(`priority` DESC) USING BTREE,
  INDEX `idx_is_read`(`is_read` ASC) USING BTREE,
  INDEX `idx_created_at`(`created_at` DESC) USING BTREE,
  INDEX `idx_user_id_created_at`(`user_id` ASC, `created_at` DESC) USING BTREE,
  INDEX `idx_user_id_is_read`(`user_id` ASC, `is_read` ASC) USING BTREE,
  CONSTRAINT `fk_user_activities_user_id` FOREIGN KEY (`user_id`) REFERENCES `users` (`id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE = InnoDB AUTO_INCREMENT = 45 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_unicode_ci COMMENT = '用户活动记录表' ROW_FORMAT = Dynamic;

